import React, {useEffect} from "react";
import "./display-inputs.css";
import {connect} from "react-redux";
import SingleInput from "../single-input";
import Spinner from "../spinner";
import PropTypes from "prop-types";
import {getInputs} from "../../actions/inputActions";

const DisplayInputs = ({userInputs, getInputs}) => {
  useEffect(() => {
    getInputs();
  }, []);

  if (loading || list === null) {
    return <Spinner />;
  }

  return (
    <div className="display-input-box">
      {list.map((input) => {
        return (
          <SingleInput
            key={input.id}
            id={input.id}
            task={input.task}
            price={input.price}
          />
        );
      })}
    </div>
  );
};

DisplayInputs.propTypes = {
  userInputs: PropTypes.arrayOf(PropTypes.object),
  getInputs: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  userInputs: state.inputs,
});
export default connect(mapStateToProps, {getInputs})(DisplayInputs);
