import {
  GET_INPUTS,
  ADD_INPUT,
  SET_LOADING,
  DELETE_INPUT,
  EDIT_INPUT,
  ADD_EDITED_INPUTS,
  INPUTS_ERROR,
} from "../actions/types";

const intialState = {
  list: null,
  editItem: {},
  editMode: false,
  loading: false,
  error: null,
};

export default (state = intialState, action) => {
  switch (action.type) {
    case GET_INPUTS:
      return {
        ...state,
        list: action.payload,
        loading: false,
      };

    case SET_LOADING:
      return {...state, loading: true};

    case ADD_INPUT:
      return {
        ...state,
        list: [
          ...state.list,
          {
            name: action.name,
            description: action.description,
            price: action.price,
          },
        ],
      };

    case DELETE_INPUT:
      return {
        ...state,
        list: [...state.list.filter((input) => input.id !== action.payload)],
        loading: false,
      };

    case EDIT_INPUT:
      return {
        ...state,
        editMode: !state.editMode,
        editItem: state.list.filter((input) => input.id === action.id),
      };

    case ADD_EDITED_INPUTS:
      return {
        editMode: !state.editMode,
        editItem: {},
        list: [
          ...state.list.filter((input) => input.id !== action.id),
          {
            id: action.id,
            task: action.task,
            price: action.price,
          },
        ],
      };

    case INPUTS_ERROR:
      console.error(`popa ${action.payload}`);
      return {
        ...state,
        error: action.payload,
      };

    default:
      return state;
  }
};
