import {
  ADD_INPUT,
  SET_LOADING,
  GET_INPUTS,
  DELETE_INPUT,
  SET_CURRENT,
  ADD_EDITED_INPUTS,
  INPUTS_ERROR,
} from "./types";

const http = "http://localhost:7070/api/services/";

//SET LOADING
export const addEditedInputs = (id, task, price) => ({
  type: "ADD_EDITED_INPUTS",
  id,
  task,
  price,
});

// GET INPUTS
export const getInputs = () => async (dispatch) => {
  try {
    setLoading();

    const res = await fetch(`${http}`);

    const data = await res.json();
    dispatch({
      type: GET_INPUTS,
      payload: data,
    });
  } catch (err) {
    dispatch({
      type: INPUTS_ERROR,
      payload: err,
    });
  }
};

// ADD NEW INPUT
export const addInput = (name, price, description) => async (dispatch) => {
  try {
    setLoading();

    const res = await fetch(`${http}`, {
      method: "POST",
      body: JSON.stringify({name, price, description}),
      headers: {
        "Content-Type": "application/json",
      },
    });
    const data = await res.json();

    dispatch({
      type: ADD_INPUT,
      payload: data,
    });
  } catch (err) {
    dispatch({
      type: INPUTS_ERROR,
      payload: err,
    });
  }
};

//DELETE INPUT
export const deleteInput = (id) => async (dispatch) => {
  try {
    setLoading();

    await fetch(`${http}:${id}`, {
      method: "DELETE",
    });

    dispatch({
      type: DELETE_INPUT,
      payload: id,
    });
  } catch (err) {
    dispatch({
      type: INPUTS_ERROR,
      payload: err,
    });
  }
};

// Update  input on server
export const editInput = (id) => async (dispatch) => {
  try {
    setLoading();

    const res = await fetch(`//${id}`, {
      method: "PUT",
      body: JSON.stringify(),
      headers: {
        "Content-Type": "application/json",
      },
    });

    const data = await res.json();

    dispatch({
      type: EDIT_INPUT,
      payload: data,
    });
  } catch (err) {
    dispatch({
      type: INPUTS_ERROR,
      payload: err,
    });
  }
};

// Set loading to true
export const setLoading = () => {
  return {
    type: SET_LOADING,
  };
};
